function h=hess7(x1,x2,lam,rho)
h=[ 2/(x1^2 + 1) - 4*lam*(x1^2 + 1) - 8*lam*x1^2 - (4*x1^2)/(x1^2 + 1)^2 + 16*rho*x1^2*(x1^2 + 1)^2 + 8*rho*x1^2*((x1^2 + 1)^2 + x2^2 - 4) + 4*rho*(x1^2 + 1)*((x1^2 + 1)^2 + x2^2 - 4),                               8*rho*x1*x2*(x1^2 + 1);
                                                                                                                                                              8*rho*x1*x2*(x1^2 + 1), 2*rho*((x1^2 + 1)^2 + x2^2 - 4) - 2*lam + 4*rho*x2^2];
end